mapper.select:
select

  distinct


from tender

  


  order by ${orderByClause}


  limit #{startRow} , #{pageSize}

   select

   project_name,start_time,classification
   from tender
   
     
   
   
     order by ${orderByClause}
   
   
     limit #{startRow} , #{pageSize}

    select
		start_time , project_name
    from tender
    
      
    
    
      order by ${orderByClause}
    
    
      limit #{startRow} , #{pageSize}

    select
		id , start_time , project_name , classification
    from tender
  	where classification <= 5
    
      order by ${orderByClause}
    
    
      limit #{startRow} , #{pageSize}

    select
		id , start_time , project_name , classification
    from tender
  	where classification > 5 and classification <= 12
    
      order by ${orderByClause}
    
    
      limit #{startRow} , #{pageSize}

    select
		id , start_time , project_name , classification
    from tender
  	where classification > 12 and classification <= 17
    
      order by ${orderByClause}
    
    
      limit #{startRow} , #{pageSize}

    select
		id , start_time , project_name , classification
    from tender
  	where classification > 17 and classification <= 20
    
      order by ${orderByClause}
    
    
      limit #{startRow} , #{pageSize}

    select
		id , start_time , project_name , classification
    from tender
  	where classification = 21
    
      order by ${orderByClause}
    
    
      limit #{startRow} , #{pageSize}

 select 
	*  
 from tender  
  where 1=1 
 
   and classification = #{classification,jdbcType=INTEGER}
 
 
   and userId = #{userId}
 
 order by start_time
 desc
 limit #{startRow} , #{pageSize}

select count(*) from tender  where 1=1 

  and classification = #{classification,jdbcType=INTEGER}


  and userId = #{userId}
